	[ExecuteAlways]
	public class SoundInEditor : MonoBehaviour
	{
	    public AudioSource _AudioSource;
	    public void Play()
	    {
	        _AudioSource.Play();
	    }

	    public static void Play(AudioSource source)
	    {
		    source.Play();
	    }
	}


